import { createStore, bindActionCreators } from "redux";
import { inc, dec, sto } from "./actions";
import reducer from "./reducers";
import 'bootswatch/dist/superhero/bootstrap.min.css'





let res = document.getElementById('res')

const update = () => {
    res.innerText = store.getState()
}

let store = createStore(reducer)

const { dispatch } = store

store.subscribe(update)

const decDispatch = () => bindActionCreators(dec, dispatch)
const incDispatch = () => bindActionCreators(inc, dispatch)
const stoDispatch = (payload) => dispatch(sto(payload))


document.getElementById('minus').addEventListener('click', decDispatch)

document.getElementById('plus').addEventListener('click', incDispatch)

document.getElementById('sto').addEventListener('click', () => {
    stoDispatch(100)
})
