import { createStore } from "redux";
import 'bootswatch/dist/superhero/bootstrap.min.css'


const reducer = (state = 0, action) => {
    switch (action.type) {
        case 'A':
            return state + 1
        case 'B':
            return  state - 1
        default:
            return state
    }
}



let state = reducer(undefined, {})

state = reducer(state, { type: 'A' })
console.log(state)
